            <table className="songList">
                <thead className="songListTable">
                <tr className="headRow">
                    <th>Name</th>
                    <th>Artist</th>
                    <th>Album</th>
                    <th>Duration</th>
                    <th>Deezer link</th>
                    <th>like</th>
                </tr>
                </thead>

                {previewSongs}

            </table>
        </div>
    );
}
