import SongItem from "./SongItem";
import {useState,useEffect} from "react";
import SongFilter from "./SongFilter";



const Songlist = (props) => {
    const [filteredArtist,setFilteredArtist] = useState('')

    const filterChangeHandler = (selectedArtist) =>{
        setFilteredArtist(selectedArtist);
    };
 const [playingNow,setPlayNow] = useState(false)
    useEffect((playingNow)=>
    {
        console.log('working')
    })
    const clickHandler = (song) =>{
     this.fontWeight= 'bold';
     setPlayNow(song.title)
        console.log(song.title)
    }


    const filteredSongList = props.songs.filter(song=>{
        return song.artist === filteredArtist;
    });

    let songsContent = props.songs;
    if (filteredSongList.length>0){
        songsContent = filteredSongList.map((song) => <SongItem
                key={song.id}
                title={song.title}
                artist={song.artist}
                album={song.album}
                duration={song.duration}
                link={song.link}
                like={song.like}
            />)}
        else{
            songsContent = props.songs.map((song) => <SongItem
                key={song.id}
                title={song.title}
                artist={song.artist}
                album={song.album}
                duration={song.duration}
                link={song.link}
                like={song.like}
    />)
            }
    return (
        <ul className="songList">
            <SongFilter
                selected={filteredArtist}
                onChangeFilter={filterChangeHandler}
                />
            <li onClick={clickHandler}>{songsContent}</li>

        </ul>
    );
}
export default Songlist;
