                artist={song.artist}
                album={song.album}
                duration={song.duration}
                link={song.link}
                like={song.like}

            />)}
        else{
            songsContent = props.songs.map((song) => <SongItem
                key={song.id}
                title={song.title}
                artist={song.artist}
                album={song.album}
                duration={song.duration}
                link={song.link}
                like={song.like}
    />)
            }
    return (
        <ul className="songList">
            <SongFilter
                selected={filteredArtist}
                onChangeFilter={filterChangeHandler}
                />
            <li
          onClick={clickHandler}>{songsContent}</li>

        </ul>
    );
}
export default Songlist;
