import SongItem from "./SongItem";
import {useState, useEffect} from "react";
import SongFilter from "./SongFilter";


const Songlist = (props) => {

    const [filteredArtist, setFilteredArtist] = useState('')

    const filterChangeHandler = (selectedArtist) => {
        setFilteredArtist(selectedArtist);
    };
    const [playingNow, setPlayNow] = useState(false)
    const [fontWeight,setFontWeight] = useState('normal')

    const clickHandler = () => {
        setFontWeight('bold')
        console.log('aaa')
        console.log(fontWeight)
        console.log(index)
    }


    const filteredSongList = props.songs.filter(song => {
        return song.artist === filteredArtist;
    });

    let songsContent = props.songs;
    if (filteredSongList.length > 0) {
        songsContent = filteredSongList.map((song,index) => <li key={index} style={{fontWeight}} onClick={() =>clickHandler(index)}><SongItem
            key={song.id}
            title={song.title}
            artist={song.artist}
            album={song.album}
            duration={song.duration}
            link={song.link}
            like={song.like}

        />
        </li>)
    } else {
        songsContent = props.songs.map((song,index) => <li key={index} onClick={clickHandler}><SongItem
            key={song.id}
            title={song.title}
            artist={song.artist}
            album={song.album}
            duration={song.duration}
            link={song.link}
            like={song.like}

        /></li>)
    }
    return (
        <ul className="songList">
            <SongFilter
                selected={filteredArtist}
                onChangeFilter={filterChangeHandler}
            />

            {songsContent}

        </ul>
    );
}
export default Songlist;
