import './App.css';
import React, {useState} from "react";
import NewSearch from "./components/newSearch/NewSearch"
import SongList from "./components/songsLogic/SongList";
import Player from "./components/player/Player";
const DUMMY_SONGS = [
        {id: 0, title: 'song 2', artist: 'blur', album: 'best of', duration: '2:22', link:'#', like:false},
        {id: 1, title: 'crying in the rain', artist: 'avi bitter', album: 'best of', duration: '3:22', link:'#', like:false},
        {id: 2, title: 'white screen', artist: 'white screen', album: 'test', duration: '5:21', link:'#', like:false}
    ];
function App() {
    const [currentSong, setCurrentSong] = useState();
    const [songs,setSongs] = useState(DUMMY_SONGS);
    // const addSongsHandler = (song) =>{
    //     setSongs((prevSongs)=>{
    //         return [song, ...prevSongs]
    //     });
    // };
    return (
        <div className="main">

            <NewSearch/>
            <Player songs={songs} currentSong={currentSong} setCurrentSong={setCurrentSong}/>
            <p>show this</p>
            <SongList songs={songs} currentSong={currentSong} setCurrentSong={setCurrentSong}/>
        </div>

    )
};
export default App;
