import SongItem from "./SongItem";
import {useEffect, useState} from "react";
import SongFilter from "./SongFilter";
import './styles/Songlist.css'


const SongList = ({songs}) => {
    const [currentSong, setCurrentSong] = useState(0);
    const [filteredArtist, setFilteredArtist] = useState('')

    const filterChangeHandler = (selectedArtist) => {
        setFilteredArtist(selectedArtist);
    };

    const ChangePlayingNow = (id) =>{
        console.log(id)
        //songs.map((song) => song.id === id ? song.playNow=true: song.playNow=false)

        setCurrentSong(id);
    }



    const filteredSongList = songs.filter(song => {
        return song.artist === filteredArtist;
    });

    var songsContent;
    if (filteredSongList.length > 0) {
        songsContent = filteredSongList.map((song) =>
            <tr key={song.id} className='insonglist' onDoubleClick={() => ChangePlayingNow(song.id)}>
            <SongItem
            song={song}
            playNow={currentSong}

        />
        </tr>)
    } else {
        songsContent = songs.map((song) => <tr key={song.id} className='insonglist' onDoubleClick={() => ChangePlayingNow(song.id)} ><SongItem
            song={song}
            playNow={currentSong}

        /></tr>)
    }
    return (
        <div>
        <SongFilter
                selected={filteredArtist}
                onChangeFilter={filterChangeHandler}
            />
        <table className="songList">
        <thead className="songListTable">
            <tr className="headRow">
            <th>Name</th>
            <th>Artist</th>
            <th>Album</th>
            <th>Duration</th>
            <th>Deezer link</th>
            <th>like</th>
                </tr>
        </thead>

            {songsContent}

        </table></div>
    );
}
export default SongList;
