import './SongItem.css'
import {useEffect, useState} from "react";
import Card from "../card/Card";
const SongItem = (props) => {
    const [liked,setLiked] = useState(props.like)
    const [clicked,setClicked] = useState('normal')
    const clickHandler = () =>{
        console.log(liked);
        console.log('1111');
        setLiked(true);
        console.log(liked);
    }
    const clickHandler2 = () => {
        setClicked('bold');
        console.log('aaa')

    }

    return (
        <div className="song"  onClick={clickHandler2} style={{clicked}}>
            <div className='title'>{props.title}</div>
            <div className="artist">{props.artist}</div>
            <div className="album">{props.album}</div>
            <div className="duration">{props.duration}</div>
            <div className="link">{props.link}</div>
            <button className="liked" onClick={clickHandler}>like</button>
        </div>

    )
}

export default SongItem;
