import {useState} from "react";

const SearchForm = (props) => {
     const [searchVal,setSearchVal] = useState('');
    const searchHandler = (event) => {
        setSearchVal(event.target.value)
    };
    const submitHandler=(event)=> {
        event.preventDefault();
        props.onSaveSearchData();
        console.log(searchVal);
        setSearchVal('');

        async function getDeezerResult() {
            const url = 'https://cors-anywhere.herokuapp.com/https://api.deezer.com/version/service/id/method/?q=' + userSearchVal;
            console.log(url);
            const response = await fetch(url, {mode: 'no-cors'});
            console.log(response)
            const data = response.json();
            console.log(data);
        }
    }

    return (<form onSubmit={submitHandler}>
            <div className='search_val'>
                <label>search songs</label>
                <input type='text' value={searchVal} onChange={searchHandler}/>
            </div>
            <div className="search_action">
                <button type="submit"  >search</button>
            </div>
        </form>
    )
};

export default SearchForm;
