import SongItem from "./SongItem";
import {useState} from "react";
import SongFilter from "./SongFilter";


const Songlist = (songs) => {

    const [filteredArtist, setFilteredArtist] = useState('')
    const [playNow, setPlayNow] = useState(false)
    const filterChangeHandler = (selectedArtist) => {
        setFilteredArtist(selectedArtist);
    };

    const changePlayingNow = (id) =>{
        console.log(songsContent)
        console.log(id)
       console.log( songsContent.map((song) => song.id === id ? setPlayNow(true): setPlayNow(false)))
    }


    const filteredSongList = songs.filter(song => {
        return song.artist === filteredArtist;
    });

    var songsContent;
    if (filteredSongList.length > 0) {
        songsContent = filteredSongList.map((song) =>
            <li key={song.id} className={song.id} onClick={() => changePlayingNow(song.id)}>
            <SongItem
            key={song.id}
            title={song.title}
            artist={song.artist}
            album={song.album}
            duration={song.duration}
            link={song.link}
            like={song.like}
            playNow={song.playNow}

        />
        </li>)
    } else {
        songsContent = songs.map((song) => <li key={song.id} className={song.id} onClick={() => changePlayingNow(song.id)} ><SongItem
            key={song.id}
            title={song.title}
            artist={song.artist}
            album={song.album}
            duration={song.duration}
            link={song.link}
            like={song.like}
            playNow={song.playNow}

        /></li>)
    }
    return (
        <ul className="songList">
            <SongFilter
                selected={filteredArtist}
                onChangeFilter={filterChangeHandler}
            />

            {songsContent}

        </ul>
    );
}
export default Songlist;
