import SongItem from "./SongItem";
import {useState} from "react";
import SongFilter from "./SongFilter";


const Songlist = (props) => {

    const [filteredArtist, setFilteredArtist] = useState('')

    const filterChangeHandler = (selectedArtist) => {
        setFilteredArtist(selectedArtist);
    };
    const [playingNow, setPlayNow] = useState(false)
    const [fontWeight,setFontWeight] = useState('normal')

    const clickHandler = (index) => {
        console.log(songsContent[index])
        //console.log(songsContent)
        //var prop = songsContent[index].props;
        //songsContent.map((song)=> song.id === index ? song.props.fontWeight = setFontWeight('bold') :  song.props.fontWeight = setFontWeight('normal'))
        songsContent[index].props.style.fontWeight=setFontWeight('bold')
        // index.target.style.fontWeight ='bold'
        // setFontWeight('bold')
        // console.log('aaa')

    }


    const filteredSongList = props.songs.filter(song => {
        return song.artist === filteredArtist;
    });

    let songsContent;
    if (filteredSongList.length > 0) {
        songsContent = filteredSongList.map((song) => <li key={song.id} className={song.id} style={{fontWeight}} onClick={()=>clickHandler(song.id)}><SongItem
            key={song.id}
            title={song.title}
            artist={song.artist}
            album={song.album}
            duration={song.duration}
            link={song.link}
            like={song.like}

        />
        </li>)
    } else {
        songsContent = props.songs.map((song) => <li key={song.id} className={song.id} style={{fontWeight}} onClick={()=>clickHandler(song.id)}><SongItem
            key={song.id}
            title={song.title}
            artist={song.artist}
            album={song.album}
            duration={song.duration}
            link={song.link}
            like={song.like}

        /></li>)
    }
    return (
        <ul className="songList">
            <SongFilter
                selected={filteredArtist}
                onChangeFilter={filterChangeHandler}
            />

            {songsContent}

        </ul>
    );
}
export default Songlist;
